INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, slug, category_id) VALUES (1710711917786, 2, '2023-10-15T21:00:00.000Z', 'AgentInsightS', 'Аналитика данных', 2, 'Улучшенные звонки с анализом и коучингом.', 'https://media.theresanaiforthat.com/icons/agentinsights.svg', 'https://media.theresanaiforthat.com/agentinsights.png', '**AgentInsights: Технологический инструмент, повышающий эффективность звонков**\nAgentInsights — это инновационный инструмент, использующий искусственный интеллект для улучшения работы отделов звонковых центров. Он призван оптимизировать взаимодействие с клиентами, обеспечивая его основами на данных, информативностью и ориентированностью на потребностях клиента.\nОсновным функционалом инструмента является приложение для записи звонков, которое фиксирует все детали звонков, исключая упущенные взаимодействия и увеличивая точность аудита и анализа. С помощью анализа звонков пользователи получают автоматическую обратную связь, оценку ИИ и текстовые транскрипты, что обеспечивает оценку эффективности и дает основу для дальнейших действий.\nAI Coaching предоставляет руководство, основанное на ИИ, для улучшения работы сотрудников и повышения качества взаимодействия с клиентами на 40%. Инструмент также обладает аналитической панелью, которая настраивает важные показатели и предоставляет ясные инсайты для принимающих решения, менеджеров и сотрудников.\nAgentInsights помогает принимать обоснованные решения на основе легко интерпретируемых данных. Каждому члену команды предоставляются индивидуальные решения. Руководители могут быстро получить доступ к данным звонков через аналитическую панель ИИ для выявления моментов, требующих похвалы и улучшений.\nМенеджеры могут улучшить результаты команды с помощью умных аналитических данных и стратегий, основанных на ИИ, чтобы направить свою команду к совершенству во взаимодействии с клиентами. Сотруд