INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, price, slug, category_id) VALUES (1710711918002, 4, '2023-10-13T21:00:00.000Z', 'Discute', 'Разработка и IT', 1, 'Виртуальный ассистент службы поддержки клиентов.', 'https://media.theresanaiforthat.com/icons/discute.svg', 'https://media.theresanaiforthat.com/discute.png', 'Discute – это AI-инструмент, который позволяет пользователям общаться со своей базой знаний, действуя как виртуальный ассистент, предоставляющий актуальную информацию и помогающий в решении проблем. Главная цель Discute – ускорить процесс поиска необходимой информации, используя базу знаний пользователя. Инструмент предлагает несколько возможностей, включая возможность вести диалог с различными источниками данных, такими как файлы PDF, DOCX и TXT.\nПользователи могут общаться со своими PDF-документами, избавляясь от необходимости вручную просматривать страницы в поисках конкретной информации. Кроме того, с помощью Discute можно анализировать файлы CSV, предоставляя возможности по анализу данных.\nНа данный момент Discute позволяет вести диалог с PDF, DOCX и TXT-документами, с ограничением размера менее 10 страниц. Однако разработчики заявляют, что предстоящие обновления добавят возможность общения с базами данных и веб-сайтами, расширяя его функциональность.\nDiscute предлагает как платные, так и бесплатные опции членства. Платная подписка обеспечивает неограниченный доступ к услугам общения, в то время как бесплатный вариант имеет определенные ограничения, включая максимум пяти диалогов и ограниченный доступ к последним возможностям.\nИнструмент акцентирует внимание на простой ценообразовании, с платной подпиской стоимостью €9,99 в месяц. В части конфиденциальности Discute заверяет пользователей, что их документы удаляются после каждого диалога, повышая уровень конфиденциальности. В целом, Discute служит ценным AI-инструментом для эффективного поиска информации и решения проблем путем использования разговоров на естественном языке с базой знаний пользователей.', 'https://www.discute.co/?ref=gighub&utm_source=gighub&utm_medium=referral', 'От €9.99/мес', 'discute', 25);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, rating, service_url, price, slug, category_id) VALUES (1710711918002, 1, '2023-10-13T21:00:00.000Z', 'Klu', 'Другое', 35, 'Разработка и улучшение Генеративные приложения', 'https://media.theresanaiforthat.com/icons/klu.svg', 'https://media.theresanaiforthat.com/klu.png', 'Klu.ai - это универсальная платформа, созданная для команд искусственного интеллекта с целью разработки, развертывания и оптимизации приложений на основе генеративного искусственного интеллекта. Сервис позволяет легко проектировать, разрабатывать и усовершенствовать функции и приложения генеративного искусственного интеллекта с помощью интуитивного интерфейса под названием Klu Studio.\nПлатформа предлагает широкий спектр интеграций, включая подключения к CRM-системам, базам данных, базам знаний, системам обработки запросов и др. С Klu команды искусственного интеллекта могут прототипировать и усовершенствовать свои проекты, используя существующие системы и данные для создания генеративных действий с контекстом по запросу.\nИнструмент поддерживает различные функции, такие как динамические и генеративные подсказки, чат GenAI с контекстом и памятью, объединение нескольких действий и использование внешних сервисов через навыки. Klu также предоставляет передовой движок данных, который позволяет пользователям создавать и масштабировать на всех основных предоставщиках больших языковых моделей.\nПлатформа предлагает аналитику использования, стоимости и производительности, позволяет наблюдать и улучшать функции с помощью передовых техник оптимизации. Klu поддерживает настройку моделей, таких как Davinci-002, GPT-3.5 Turbo и GPT-4 с использованием пользовательских данных, обеспечивая при этом сохранность данных и переносимость. Разработчики могут воспользоваться инструментами, ориентированными на разработчиков, для Python, TypeScript и пользовательского инте