INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, slug, category_id) VALUES (1710711918240, 6, '2023-10-12T21:00:00.000Z', 'µScale', 'Изображения', 24, 'Улучшено качество изображения и размер с помощью масштабирования.', 'https://media.theresanaiforthat.com/icons/scale.svg', 'https://media.theresanaiforthat.com/scale.png', '**Сервис увеличения изображений µScale**\nµScale — это инструмент с открытым исходным кодом, использующий технологию искусственного интеллекта для улучшения качества изображений путем увеличения их размера до 4 раз от исходных размеров.\nСервис поддерживает различные форматы изображений, включая JPG, PNG и WEBP, и имеет максимальный лимит размера файла 4 МБ. С простым и понятным интерфейсом µScale позволяет пользователям выбирать изображение или вставлять его непосредственно в инструмент для увеличения размера. Разработанный adevinwild для FullstackNetwork Hackathon #1, µScale основывается на интеграции технологий Next.js 13, Vercel, shadcn/ui и Replicate.\nКак проект с открытым исходным кодом, µScale предоставляет пользователям свободу доступа и изменения его кода, что позволяет вносить вклад в разработку инструмента или настраивать его в соответствии с конкретными требованиями.\nЦелью µScale является обеспечение пользователям возможности улучшить качество изображений низкого разрешения без ущерба для их визуальной целостности. С помощью алгоритмов искусственного интеллекта сервис интеллектуально увеличивает изображения, предоставляя удобный способ увеличить размер и качество изображений для различных приложений.', 'https://micro-scale.vercel.app/?ref=gighub&utm_source=gighub&utm_medium=referral', 'µscale', 10);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, rating, service_url, slug, category_id) VALUES (1710711918241, 4, '2023-10-12T21:00:00.000Z', 'Yearbook Photos', 'Изображения', 29, 'Фотографии для альбома моментально улучшены.', 'https://media.theresanaiforthat.com/icons/yearbook-photos.svg', 'https://media.theresanaiforthat.com/yearbook-photos.png', 'Генератор фотографий для выпускных альбомов - это сервис, использующий искусственный интеллект для мгновенного создания фотографий для выпускного альбома. Он предлагает быстрое и удобное решение для захвата неповторимых моментов уникальным образом.\nДанный инструмент доступен каждому и полностью бесплатен для использования. Процесс начинается с оригинального фото лица человека, которое служит в качестве входных данных. Затем сервис использует технологии искусственного интеллекта для генерации соответствующей фотографии для выпускного альбома. Эта созданная фотография передает суть традиционного портрета для выпускного альбома, гарантируя профессиональный и аккуратный результат.\nХотя отзывы пользователей в настоящее время отсутствуют, пользователей призывают принять активное участие, делясь своими впечатлениями в социальных сетях с использованием хэштега #yearbookphotos. Эта интерактивная функция позволяет пользователям общаться и делиться своим мнением о работе инструмента.\nГенератор фотографий для выпускного альбома работает на базе ведущих поставщиков ИИ, включая Replicate, Bytescale и Vercel. Такие партнерства обеспечивают надежность и эффективность инструмента в создании качественных фотографий для выпускного альбома. В заключение, генератор фотографий для выпускного альбома - это бесплатный сервис, работающий на базе искусственного интеллекта, который позволяет пользователям мгновенно создавать фотографии для выпускного альбома.\nОн использует передовые технологии для создания профессиональных изображений на основе оригинальной фотографии. Хотя отзывы пользователей пока отсутствуют, пользователи могут взаимодействовать, делясь своим опытом в социальных сетях. Этот инструмент поддерживается авторитетными поставщиками ИИ, что гарантирует надежный и эффективный опыт использования.', 5, 'https://www.yearbookphotos.io/?ref=gighub&utm_source=gighub&utm_medium=referral', 'yearbook-photos', 10);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, rating, service_url, price, slug, category_id) VALUES (1710711918248, 9, '2023-10-11T21:00:00.000Z', 'AI Headshot Generator', 'Изображения', 45, 'Профессиональные портреты для бизнеса.', 'https://media.theresanaiforthat.com/icons/ai-headshot-generator.svg', 'https://media.theresanaiforthat.com/ai-headshot-generator.png', 'Генератор AI-портретов - это профессиональный стартовый набор, оснащенный Next.js, Leap AI и Vercel. Это проект с открытым исходным кодом от компании Leap AI, который позволяет пользователям создавать профессиональные AI-портреты всего за несколько минут.\nПриложение использует Leap AI для обучения модели и создания портретов. Оно также использует Next.js для разработки приложения и страницы приветствия, Supabase для базы данных и аутентификации, Resend (опционально) для отправки пользователям уведомлений, когда портреты готовы, Shadcn с Tailwind CSS для стилизации, Vercel для развертывания и Stripe для выставления счетов.\nС помощью этого инструмента пользователи могут легко клонировать, настроить и запустить свой собственный сервис AI-портретов.\nОбучение моделей доступно только на платных планах, поэтому пользователям необходим активный план Leap AI для обучения моделей. Процесс установки включает настройку Supabase/Vercel и репозитория GitHub пользователя с использованием предоставленной кнопки развертывания Vercel.\nВо время интеграции с Supabase пользователи должны убедиться, что выбрали опцию Создать образцовые таблицы, это может занять несколько минут. В общем, Генератор AI-портретов предоставляет упрощенное решение для создания профессиональных портретов с использованием технологии искусственного интеллекта. Он объединяет различные технологии и сервисы, создавая комплексную и эффективную платформу для генерации портретов.', 1, 'https://vercel.com/templates/next.js/headshot-ai?ref=gighub&utm_source=gighub&utm_medium=referral', 'Бесплатно', 'ai-headshot-generator', 10);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, slug, category_id) VALUES (1710711918248, 3, '2023-10-11T21:00:00.000Z', 'Pelery', 'Текст', 1, 'Повторное использование контента из рассылки в социальных сетях.', 'https://media.theresanaiforthat.com/icons/pelery.svg', 'https://media.theresanaiforthat.com/pelery.png', 'Pelery - это универсальная платформа для развития, разработанная для помощи пользователям в повторном использовании своего контента из рассылок для социальных сетей. Подключив свой аккаунт Substack, пользователи могут автоматически создавать новые посты и делиться ими на различных социальных платформах.\nИнструмент предлагает функции редактирования и настройки созданного контента, что позволяет пользователям изменять тон, длину, использование эмодзи и хэштегов. Pelery использует технологию искусственного интеллекта для понимания уникального стиля и тона письма из первоначальной рассылки и создания контента для социальных медиа соответственно.\nЭто гарантирует последовательность в сообщениях на разных платформах. Платформа призвана помочь пользователям увеличить вовлеченность аудитории рассылок и расширить свою базу подписчиков.\nСогласно отзывам, Pelery получил положительные отклики от пользователей, которые заметили улучшения в своем бизнесе и повышение самооценки после использования платформы.\nИзвестные создатели Substack, такие как Swiggy, CMU, Gojek, JP Morgan Chase, UC Berkeley и New Clari, рекомендуют использовать Pelery для развития рассылок. Для начала работы пользователи могут просто подключить свой аккаунт Substack, и Pelery автоматически извлечет все посты.\nПлатформа предлагает простой в использовании редактор, который позволяет настраивать созданный контент перед его публикацией в социальных медиа. Pelery также предлагает пробный период бесплатного пользования, позволяя пользователям опробовать инструмент в течение 30 дней и ощутить его преимущества на собственном опыте.', 'https://pelery.unicornplatform.page/growth/?ref=gighub&utm_source=gighub&utm_medium=referral', 'pelery', 11);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, rating, service_url, price, slug, category_id) VALUES (1710711918248, 5, '2023-10-11T21:00:00.000Z', 'SBhelper', 'Обслуживание и поддержка клиентов', 4, 'Улучшение обслуживания малого бизнеса.', 'https://media.theresanaiforthat.com/icons/sbhelper.svg', 'https://media.theresanaiforthat.com/sbhelper.png', 'Small Business AI Chat - это сервис поддержки, работающий на основе искусственного интеллекта и специально разработанный для малого бизнеса. Его основная цель - улучшить обслуживание клиентов, обеспечивая доступность 24/7 и освобождая владельцев бизнеса для концентрации на развитии своего предприятия.\nОсновные возможности Small Business AI Chat включают многоязычную поддержку, что позволяет адаптироваться к языку каждого клиента, а также способность постоянно учиться и улучшаться благодаря искусственному интеллекту. Сервис адаптируется к каждому индивидуальному бизнесу и создан таким образом, чтобы взаимодействие с ним было как с членом команды.\nУстановка Small Business AI Chat предельно проста: пользователи регистрируются, указывают данные платежной информации и описывают свой бизнес, чтобы чат-бот смог научиться максимально большому количеству информации. Добавление чата на веб-сайт аналогично встраиванию видеоролика на YouTube.\nИспользуя Small Business AI Chat, владельцы бизнеса могут сократить затрачиваемое время, поскольку сервис способен отвечать на 85% вопросов, касающихся их деятельности. Это позволяет им сконцентрироваться на более важных задачах, обслуживать больше клиентов и улучшать коммуникацию с ними, предоставляя моментальную и точную информацию.\nSmall Business AI Chat предлагает различные тарифные планы, подходящие для различных потребностей бизнеса и бюджетов. Сервис легко встраивается на любой веб-сайт, независимо от того, создан он на таких платформах, как Webflow, Wix, или WordPress, или на специализированном веб-приложении. Кроме того, возможность обновления информации о бизнесе и направления клиентов на адреса электронной почты или телефоны для индивидуальных случаев делает его максимально адаптивным и удобным.\nВ общем, Small Business AI Chat - это удобный и эффективный инструмент, который улучшает обслуживание клиентов и позволяет малым предприятиям развиваться и процветать.', 4.5, 'https://sbhelper.ai/?ref=gighub&utm_source=gighub&utm_medium=referral', 'От $5/мес', 'sbhelper', 22);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, slug) VALUES (1710711918249, 2, '2023-10-11T21:00:00.000Z', 'Appmanager', 'Управление продуктами', 2, 'Предоставление доступа пользователю и отслеживание издержек для стартапов.', 'https://media.theresanaiforthat.com/icons/appmanager.svg', 'https://media.theresanaiforthat.com/appmanager.png', 'AppManager – это искусственный интеллектуальный агент для ИТ, разработанный для стартапов, с целью оптимизации процессов предоставления приложений и управления пользователями. Его главная задача – сделать предоставление приложений и управление пользователями легким и доступным по цене.\nС помощью AppManager пользователи могут эффективно управлять учетными записями пользователей в различных приложениях, экономя время и усилия, которые обычно тратятся на индивидуальное управление пользователями.\nКроме того, этот инструмент предлагает функции управления подписками, позволяя пользователям легко отслеживать и управлять стоимостью приложений, такие как повышение, понижение или отмена подписок, непосредственно из платформы AppManager.\nAppManager также предоставляет аналитику по затратам на пользователей, детально разбирая стоимость подписки на приложение для каждого сотрудника, что может помочь компаниям принимать более обоснованные решения относительно своих расходов.\nВ плане управления приложениями инструмент централизует разбросанные счета, объединяя их в одном месте для легкого контроля над расходами компании. Управление разрешениями и обновление способов оплаты также могут осуществляться из платформы AppManager. Инструмент сосредотачивается на упрощении административных задач, принося эффективность в процессы предоставления пользователей, учета расходов и управления приложениями.\nЦель AppManager заключается в предоставлении централизованного решения для стартапов, освобождая время и ресурсы, чтобы бизнес мог сосредоточиться на своем развитии и стратегических операциях.', 'https://appmanager.ai/?ref=gighub&utm_source=gighub&utm_medium=referral', 'appmanager');
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, price, slug, category_id) VALUES (1710711918249, 3, '2023-10-11T21:00:00.000Z', 'Product Ad', 'Изображения', 2, 'Созданные профессиональные рекламные объявления о продуктах.', 'https://media.theresanaiforthat.com/icons/product-ad.svg', 'https://media.theresanaiforthat.com/product-ad.png', 'Modifyla - это ИИ-сервис, разработанный для помощи пользователям в быстром и легком создании профессиональных рекламных изображений продуктов. Платформа позволяет загружать изображение продукта и описывать желаемый результат.\nСервис Modifyla применяет свои обработочные возможности для преобразования изображения в соответствии с предоставленным описанием. Пользователи проходят четырехэтапный процесс: загрузка изображения, описание желаемого результата, ожидание магии искусственного интеллекта и, наконец, наслаждение созданными изображениями.\nПомимо этого, Modifyla предлагает пользователям бесплатное пробное использование при создании учетной записи, позволяя им сгенерировать 10 изображений без платы. После окончания пробного периода пользователи могут выбрать один из трех тарифных планов.\nСтартовый план предлагает 100 изображений в месяц для индивидуальных пользователей по цене $0.0499 за изображение. Стандартный план, предназначенный для малого бизнеса, предоставляет 500 изображений в месяц по цене $0.0400 за изображение.\nПремиум-план, созданный для корпоративных клиентов, включает 1500 изображений в месяц по цене $0.0333 за изображение. Хотя в тексте не упоминаются конкретные планы по обновлениям и дополнительным функциям, пользователи могут связаться с Modifyla по адресу [emailprotected] для получения дополнительной информации.\nИспользуя Modifyla, пользователи могут быстро и легко создавать высококачественные рекламные изображения продуктов, экономя время и ресурсы. Сервис обладает простым в использовании интерфейсом и предлагает разнообразные тарифные планы, соответствуя различным потребностям клиентов.', 'https://productad.co/?ref=gighub&utm_source=gighub&utm_medium=referral', 'От $7.99/мес', 'product-ad', 10);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, price, slug, category_id) VALUES (1710711918250, 1, '2023-10-11T21:00:00.000Z', 'NIMBO', 'Другое', 3, 'Мобильный чат-бот для общения и выполнения задач.', 'https://media.theresanaiforthat.com/icons/nimbo.svg', 'https://media.theresanaiforthat.com/nimbo.png', 'ИИ-сервис NIMBO AI Advanced Chatbot — это мобильное приложение, доступное в App Store для устройств iPhone, iPad и iPod touch, предоставляющее пользователю возможность взаимодействовать с продвинутым чат-ботом.\nПриложение позволяет задавать вопросы или давать команды чат-боту, который отвечает на них соответствующей информацией или выполняет определенные задачи. NIMBO AI Advanced Chatbot обладает удобным интерфейсом для легкого взаимодействия и навигации с чат-ботом.\nПользователи могут скачать приложение из App Store и использовать его на своих устройствах iOS. Цель чат-бота заключается в предоставлении улучшенного опыта общения, используя технологию искусственного интеллекта для понимания и реагирования на запросы или вопросы пользователей.\nСервис разработан для помощи пользователям в поиске информации или выполнении задач более эффективно через общение на естественном языке. Функционал приложения может включать в себя распознавание речи, обработку естественного языка и алгоритмы машинного обучения для повышения точности и эффективности ответов чат-бота со временем.\nЭти технологии позволяют чат-боту понимать намерения пользователей, генерировать соответствующие ответы и приспосабливаться к предпочтениям или образцам поведения пользователей. В целом, NIMBO AI Advanced Chatbot нацелен на предоставление удобного и эффективного способа взаимодействия с помощником на основе искусственного интеллекта, облегчая доступ к информации или выполнение задач через общение на естественном языке на устройствах iOS.', 'https://apps.apple.com/us/app/nimbo-ai-advanced-chatbot/id6445877276?ref=gighub&utm_source=gighub&utm_medium=referral', 'Бесплатно + от $1.99', 'nimbo', 5);
