INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, price, slug, category_id) VALUES (1710711919624, 3, '2023-10-01T21:00:00.000Z', 'Buska', 'Текст', 2, 'Мониторинг упоминаний бренда в реальном времени для оценки репутации.', 'https://media.theresanaiforthat.com/icons/buska.svg', 'https://media.theresanaiforthat.com/buska.png', 'Buska.io – это сервис, основанный на искусственном интеллекте, который предназначен для помощи бизнесу в мониторинге и отслеживании упоминаний их бренда в онлайн-пространстве на различных платформах, включая интернет и социальные сети.\nС помощью Buska.io пользователи могут быть в курсе того, что говорят об их бренде в реальном времени. Сервис позволяет легко получить доступ ко всем упоминаниям бренда с различных платформ на одном ресурсе, предоставляя полный обзор их онлайн-присутствия.\nИспользуя Buska.io, компании могут эффективно управлять репутацией бренда и получить ценные инсайты отзывов и мнений клиентов. Технология устраняет необходимость вручных поисков или полагания на традиционные поисковые системы, позволяя пользователям экономить время и усилия.\nBuska.io предлагает удобный пользовательский интерфейс, который облегчает навигацию и доступ ко всем актуальным упоминаниям, давая возможность компаниям оставаться на связи со своей аудиторией и оперативно реагировать на комментарии, рекомендации и запросы, касающиеся их бренда.\nС возможностью автоматического отслеживания и мониторинга упоминаний о бренде, Buska.io становится незаменимым инструментом для управления брендом в реальном времени. Он позволяет компаниям, как малым, так и крупным, эффективно отслеживать и анализировать свое онлайн-присутствие, облегчая разработку эффективных маркетинговых стратегий и принятие осознанных решений.\nВ общем, Buska.io предоставляет компаниям мощное решение для мониторинга онлайн-упоминаний о их бренде, позволяя им быть в курсе общественного мнения, взаимодействовать со своей аудиторией и активно управлять репутацией бренда.', 'https://www.buska.io/?ref=gighub&utm_source=gighub&utm_medium=referral', 'Бесплатно + от $15/мес', 'buska', 11);
INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, service_url, slug, category_id) VALUES (1710711919625, 4, '2023-10-01T21:00:00.000Z', 'Prompto', 'Изображения', 10, 'Ассистент для поиска и организации фотографий в офлайн-режиме', 'https://media.theresanaiforthat.com/icons/prompto.svg', 'https://media.theresanaiforthat.com/prompto.png', '**Chat Gallery: AI-powered Offline Gallery Assistant**\n**Chat Gallery** – это приложение с искусственным интеллектом для поиска фото в офлайне, которое использует передовые технологии ИИ для обеспечения удобного и точного поиска фотографий. Пользователи могут легко находить изображения на своих устройствах, просто вводя ключевое слово.\nПриложение предлагает ряд ключевых функций. Оно интегрируется с TensorFlow и OpenAICLIP, используя мощную модель ИИ для распознавания сцен и категоризации фотографий. Кроме того, оно позволяет осуществлять поиск изображений внутри галереи похожих или связанных фото.\n*Chat Gallery* работает офлайн, поэтому пользователи могут выполнять поиск фото в любое время и в любом месте, не требуя подключения к интернету. Приложение автоматически организует фотографии на основе ключевых слов и соответствия изображения и фото, обеспечивая умные возможности сортировки. В дополнение к стандартным функциям поиска, *Chat Gallery* позволяет искать по цветам, местоположениям, людям, животным, эмоциям и другим элементам.\nЭто предоставляет удобный способ категоризации и поиска фотографий в различных сценариях, будь то вспоминая восход солнца на пляже, ища снимки восхождения на гору с друзьями, пересматривая уикенд на барбекю дома или отыскивая моменты прогулок с собакой в парке.\n*Chat Gallery* придерживается безопасности данных, не передавая их третьим лицам и не собирая их самостоятельный. Приложение регулярно обновляется, обеспечивая непрерывное совершенствование и удовлетворенность пользователей.', 'https://play.google.com/store/apps/details?id=com.cooke.picklight&ref=gighub&utm_source=gighub&utm_medium=referral', 'prompto', 10);