INSERT INTO ai_services (id, sort_order, date_added, title, ai_category, bookmarks_count, short_description_ru, logo_url, cover_url, full_description_ru, rating, service_url, slug, category_id) VALUES (1710711916969, 3, '2023-10-22T21:00:00.000Z', 'Autolicious', 'Другое', 15, 'Органайзер и категоризатор закладок', 'https://media.theresanaiforthat.com/icons/autolicious.png', 'https://media.theresanaiforthat.com/autolicious.png', '**Autolicious: Chrome Extension for Automated Bookmark Organization**\nAutolicious - это расширение для браузера Chrome, которое автоматически организует закладки с помощью технологий ChatGPT и OpenAI API. С помощью одного нажатия кнопки пользователи могут добавить закладку, и Autolicious автоматически добавит категорию, подкатегорию и описание.\nОднако стоит отметить, что использование API от OpenAI связано с определенной стоимостью, начиная с приблизительно $0.0015 за добавление закладки для модели gpt-3.5-turbo.\nДля использования расширения пользователи должны предоставить свой ключ API OpenAI, который они могут получить, зарегистрировавшись на платформе OpenAI и создав API-ключ.\nAutolicious отправляет содержимое страницы на OpenAI API для категоризации, и OpenAI заявляет, что обеспечивает безопасность пользовательских данных. Однако, если у пользователей есть опасения по поводу конфиденциальности и безопасности своей истории просмотров, рекомендуется воздержаться от использования этого расширения. В настоящее время Autolicious поддерживает только просмотр сохраненных закладок и не предлагает возможности ручной организации.\nЭта версия расширения считается находящейся на начальной стадии разработки. Для получения дополнительной информации пользователи могут прочитать дополнительную информацию, сообщить о злоупотреблении и получить доступ к политике конфиденциальности разработчика.\nВажно отметить, что разработчик не определил себя как торговца, и права потребителей могут не применяться к контрактам с этим разработчиком в Европейском сою